const GOOGLE_SHEET_URL = 'VOTRE_URL_ICI';

const OrderForm: React.FC = () => {
  const [formData, setFormData] = useState({
    fullname: '',
    phone: '',
    address: '',
    city: '',
    size: 'M',
    color: 'Noir'
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError(null);

    try {
      // Si l'URL n'est pas configur√©e, on simule pour la d√©mo
      if (GOOGLE_SHEET_URL === 'VOTRE_URL_ICI') {
        console.warn("L'URL Google Sheet n'est pas configur√©e. Simulation de l'envoi...");
        await new Promise(resolve => setTimeout(resolve, 1500));
        setIsSubmitting(false);
        setIsSuccess(true);
        return;
      }

      const response = await fetch(GOOGLE_SHEET_URL, {
        method: 'POST',
        mode: 'no-cors', // Important pour Google Apps Script
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      // Avec mode: 'no-cors', on ne peut pas lire la r√©ponse, 
      // mais si l'appel ne crash pas, on consid√®re que c'est bon.
      setIsSubmitting(false);
      setIsSuccess(true);
    } catch (err) {
      console.error("Erreur lors de l'envoi:", err);
      setError("Une erreur est survenue. Veuillez r√©essayer ou nous contacter sur WhatsApp.");
      setIsSubmitting(false);
    }
  };

  if (isSuccess) {
    return (
      <section id="order-form" className="py-20 px-4 bg-white">
        <div className="max-w-xl mx-auto text-center p-10 rounded-3xl border-2 border-green-100 bg-green-50">
          <CheckCircle2 size={80} className="text-green-500 mx-auto mb-6" />
          <h2 className="text-3xl font-bold text-gray-900 mb-4">Commande Re√ßue !</h2>
          <p className="text-gray-600 mb-8">
            Merci {formData.fullname.split(' ')[0]}. Votre commande a bien √©t√© enregistr√©e dans notre syst√®me. Notre service client vous appellera sur le <strong>{formData.phone}</strong> pour confirmer l'exp√©dition.
          </p>
          <button 
            onClick={() => setIsSuccess(false)}
            className="text-green-600 font-bold hover:underline"
          >
            Passer une autre commande
          </button>
        </div>
      </section>
    );
  }

  return (
